mapper.select:
select bw.* , bk.name as bookName, bk.author as author, bk.publish as publish, bk.edition as edition, ct.name AS categoryName from book_borrow.borrow bw left join book_borrow.book bk on bw.book_id = bk.id left join book_borrow.category ct on bk.category_id = ct.id
and bw.user_id = #{userId}
and bk.category_id = #{categoryId}
and bw.start_time = #{startTime}
and bw.end_time = #{endTime}

select bw.* , bk.name as bookName, bk.author as author, bk.publish as publish, bk.edition as edition, ct.name AS categoryName, bu.name as userName from book_borrow.borrow bw left join book_borrow.book bk on bw.book_id = bk.id left join book_borrow.category ct on bk.category_id = ct.id left join book_borrow.user bu on bw.user_id = bu.id
and bw.status = 0
and bw.user_id = #{userId}
and bu.name = #{userName}
and ct.name = #{categoryName}
and bk.category_id = #{categoryId}
and bw.start_time = #{startTime}
and bw.end_time = #{endTime}

mapper.delete:
DELETE FROM book_borrow.borrow
WHERE id IN

    #{borrowId}
